import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Typography, Button, Card, CardContent, CircularProgress } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { motion } from 'framer-motion';

const UserDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setUser(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">Error loading user details.</Typography>;

  return (
    <motion.div 
      style={{ padding: '20px' }}
      initial={{ x: '-100vw' }}
      animate={{ x: 0 }}
      transition={{ type: 'spring', stiffness: 50 }}
    >
      <Button onClick={() => navigate(-1)} variant="contained" color="primary" startIcon={<ArrowBackIcon />}>
        Go Back
      </Button>
      {user && (
        <Card>
          <CardContent>
            <Typography variant="h4">{user.name}</Typography>
            <Typography>Email: {user.email}</Typography>
            <Typography>Phone: {user.phone}</Typography>
            <Typography>Company: {user.company.name}</Typography>
            <Typography>Website: {user.website}</Typography>
          </CardContent>
        </Card>
      )}
    </motion.div>
  );
};

export default UserDetail;
