import React, { useState, useEffect } from 'react';
import { Search, Moon, Sun } from 'lucide-react';
import { Card, CardHeader, CardContent } from '@mui/material';
import { Alert, AlertTitle, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const UserCard = ({ user }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/user/${user.id}`);
  };

  return (
    <Card
      className="h-full transition-all hover:shadow-xl cursor-pointer"
      style={{
        background: 'rgba(255, 255, 255, 0.9)',
        backdropFilter: 'blur(10px)',
        borderRadius: '12px',
      }}
      onClick={handleClick}
    >
      <CardHeader>
        <div className="flex items-center space-x-2">
          <div
            className="h-10 w-10 rounded-full flex items-center justify-center"
            style={{
              background: 'linear-gradient(to right, #ff7e5f, #feb47b)',
            }}
          >
            <span className="text-white font-medium">{user.name[0]}</span>
          </div>
          <div>
            <h3 className="text-lg font-semibold">{user.name}</h3>
            <p className="text-sm text-gray-500 dark:text-gray-400">{user.email}</p>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-2">
          <p className="text-sm">
            <span className="font-medium">Company:</span> {user.company.name}
          </p>
          <p className="text-sm">
            <span className="font-medium">City:</span> {user.address.city}
          </p>
        </div>
      </CardContent>
    </Card>
  );
};

const Home = ({ toggleTheme, darkMode }) => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      setLoading(true);
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        setUsers(data);
      } catch (err) {
        setError('Failed to load users. Please try again later.');
      }
      setLoading(false);
    };
    fetchUsers();
  }, []);

  const filteredUsers = users.filter((user) =>
    user.name.toLowerCase().includes(search.toLowerCase()) ||
    user.email.toLowerCase().includes(search.toLowerCase())
  );

  if (error) {
    return (
      <div className="flex justify-center items-center h-screen">
        <Alert severity="error">
          <AlertTitle>Error</AlertTitle>
          {error}
        </Alert>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">User Directory</h1>
        <button
          onClick={toggleTheme}
          className="p-2 rounded-full"
          style={{
            background: darkMode
              ? 'linear-gradient(to right, #0f2027, #203a43, #2c5364)'
              : 'linear-gradient(to right, #ff7e5f, #feb47b)',
            color: '#fff',
          }}
        >
          {darkMode ? <Sun /> : <Moon />}
        </button>
      </div>

      <div className="relative mb-6">
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
        <input
          type="text"
          placeholder="Search users..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2"
        />
      </div>

      {loading ? (
        <div className="flex justify-center py-12">
          <CircularProgress />
        </div>
      ) : (
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {filteredUsers.map((user) => (
            <UserCard key={user.id} user={user} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
