import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Typography, Button, Card, CardContent, CircularProgress } from '@mui/material';

const UserDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setUser(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">Error loading user details.</Typography>;

  return (
    <div style={{ padding: '20px' }}>
      <Button onClick={() => navigate(-1)} variant="contained" color="primary">
        Go Back
      </Button>
      {user && (
        <Card>
          <CardContent>
            <Typography variant="h4">{user.name}</Typography>
            <Typography>Email: {user.email}</Typography>
            <Typography>Phone: {user.phone}</Typography>
            <Typography>Company: {user.company.name}</Typography>
            <Typography>Website: {user.website}</Typography>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default UserDetail;
