import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Typography, TextField, Button, Card, CardContent, Grid, CircularProgress } from '@mui/material';

const Home = ({ toggleTheme }) => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((data) => {
        setUsers(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err);
        setLoading(false);
      });
  }, []);

  const filteredUsers = users.filter((user) =>
    user.name.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">Error loading users.</Typography>;

  return (
    <div style={{ padding: '20px' }}>
      <Button onClick={toggleTheme} variant="contained" color="primary">
        Toggle Dark/Light Mode
      </Button>
      <Typography variant="h4" gutterBottom>
        User Directory
      </Typography>
      <TextField
        label="Search Users"
        variant="outlined"
        fullWidth
        margin="normal"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <Grid container spacing={3}>
        {filteredUsers.map((user) => (
          <Grid item xs={12} sm={6} md={4} key={user.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{user.name}</Typography>
                <Typography>Email: {user.email}</Typography>
                <Typography>City: {user.address.city}</Typography>
                <Button component={Link} to={`/user/${user.id}`} variant="contained" color="secondary">
                  View Details
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default Home;
